// Read an object from a file and return it as a string
public static string DeserializeObjectToString<T>(this String filename)
{
    XmlSerializer xmlSerializer = new XmlSerializer(typeof(T));
    TextReader textReader = new StreamReader(filename);
    XmlReader xmlReader = XmlReader.Create(textReader);
    T deserializedObject = (T)xmlSerializer.Deserialize(xmlReader);
    textReader.Close();

    // Convert the deserialized object to a string
    string serializedString = SerializeObjectToString(deserializedObject);

    return serializedString;
}

private static string SerializeObjectToString<T>(T toSerialize)
{
    XmlSerializer xmlSerializer = new XmlSerializer(toSerialize.GetType());
    StringWriter stringWriter = new StringWriter();
    xmlSerializer.Serialize(stringWriter, toSerialize);
    return stringWriter.ToString();
}
```

